mapper.update:
UPDATE send_message_from_gateway_to_telecom
SET MESSAGE_STATUS = #{message_status},
    RESULT         = #{result}
WHERE SEND_ID = #{send_id};

mapper.select:
SELECT message_type, message_status, send_id, received_id, result
FROM send_message_from_gateway_to_telecom
WHERE SEND_ID = #{send_id}
